<?php if(!defined('CMS_ROOT')) die;$files_dir_path = PUBLIC_ROOT.DIRECTORY_SEPARATOR.'page_images';// Make directory in public folderif ( ! is_dir($files_dir_path))	mkdir($files_dir_path);// Right chmod$iterator = new RecursiveIteratorIterator(new RecursiveDirectoryIterator($files_dir_path), RecursiveIteratorIterator::SELF_FIRST); foreach ($iterator as $item) chmod($item, 0777);